def submit(self, on_element=None):
    """
    submit a form.

    :Args:
     - on_element: The key to submit.
       If None, submits the currently active form.
    """
    if on_element:
        self._execute(Command.SUBMIT, {'id': on_element})
    else:
        self._execute(Command.SUBMIT)
